import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MemberModal from '../components/MemberModal';
import PaymentModal from '../components/PaymentModal';
import { exportToCSV, formatCurrency } from '../utils/helpers';

const API_URL = 'http://127.0.0.1:8080/api';

const Members = () => {
  const [members, setMembers] = useState([]);
  const [filteredMembers, setFilteredMembers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showMemberModal, setShowMemberModal] = useState(false);
  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [selectedMember, setSelectedMember] = useState(null);
  const [message, setMessage] = useState({ type: '', text: '' });

  const [filters, setFilters] = useState({
    academicYear: '2024-2025',
    hasPaid: '',
    status: '',
    search: ''
  });

useEffect(() => {
  fetchMembers();
  // eslint-disable-next-line react-hooks/exhaustive-deps
}, []);

useEffect(() => {
  filterMembers();
  // eslint-disable-next-line react-hooks/exhaustive-deps
}, [members, filters]);

  const fetchMembers = async () => {
    try {
      setLoading(true);
      const res = await axios.get(`${API_URL}/members`);
      setMembers(res.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching members:', error);
      showMessage('error', 'Failed to fetch members');
      setLoading(false);
    }
  };

  const filterMembers = () => {
    let filtered = [...members];

    if (filters.academicYear) {
      filtered = filtered.filter(m => m.academicYear === filters.academicYear);
    }

    if (filters.hasPaid !== '') {
      filtered = filtered.filter(m => m.hasPaid === (filters.hasPaid === 'true'));
    }

    if (filters.status) {
      filtered = filtered.filter(m => m.status === filters.status);
    }

    if (filters.search) {
      const searchLower = filters.search.toLowerCase();
      filtered = filtered.filter(m => 
        m.firstName.toLowerCase().includes(searchLower) ||
        m.lastName.toLowerCase().includes(searchLower) ||
        m.studentId.toLowerCase().includes(searchLower) ||
        m.email.toLowerCase().includes(searchLower)
      );
    }

    setFilteredMembers(filtered);
  };

  const handleFilterChange = (e) => {
    setFilters({
      ...filters,
      [e.target.name]: e.target.value
    });
  };

  const handleAddMember = async (formData) => {
    try {
      await axios.post(`${API_URL}/members`, formData);
      showMessage('success', 'Member added successfully');
      setShowMemberModal(false);
      fetchMembers();
    } catch (error) {
      showMessage('error', error.response?.data?.message || 'Failed to add member');
    }
  };

  const handleEditMember = async (formData) => {
    try {
      await axios.put(`/api/members/${selectedMember._id}`, formData);
      showMessage('success', 'Member updated successfully');
      setShowMemberModal(false);
      setSelectedMember(null);
      fetchMembers();
    } catch (error) {
      showMessage('error', error.response?.data?.message || 'Failed to update member');
    }
  };

  const handleUpdatePayment = async (paymentData) => {
    try {
      await axios.put(`/api/members/${selectedMember._id}/payment`, paymentData);
      showMessage('success', 'Payment status updated successfully');
      setShowPaymentModal(false);
      setSelectedMember(null);
      fetchMembers();
    } catch (error) {
      showMessage('error', 'Failed to update payment status');
    }
  };

  const handleDeleteMember = async (id) => {
    if (window.confirm('Are you sure you want to delete this member?')) {
      try {
        await axios.delete(`/api/members/${id}`);
        showMessage('success', 'Member deleted successfully');
        fetchMembers();
      } catch (error) {
        showMessage('error', 'Failed to delete member');
      }
    }
  };

  const showMessage = (type, text) => {
    setMessage({ type, text });
    setTimeout(() => setMessage({ type: '', text: '' }), 3000);
  };

  const handleExport = () => {
    exportToCSV(filteredMembers, `members_${filters.academicYear}.csv`);
  };

  const openAddModal = () => {
    setSelectedMember(null);
    setShowMemberModal(true);
  };

  const openEditModal = (member) => {
    setSelectedMember(member);
    setShowMemberModal(true);
  };

  const openPaymentModal = (member) => {
    setSelectedMember(member);
    setShowPaymentModal(true);
  };

  return (
    <div className="container">
      <div className="card">
        <div className="flex-between mb-20">
          <h1>Members Management</h1>
          <div className="flex-center">
            <button className="btn btn-success" onClick={handleExport}>
              Download List
            </button>
            <button className="btn btn-primary" onClick={openAddModal}>
              Add New Member
            </button>
          </div>
        </div>

        {message.text && (
          <div className={`alert alert-${message.type}`}>
            {message.text}
          </div>
        )}

        <div className="filters">
          <div className="filter-group">
            <label>Academic Year</label>
            <select
              name="academicYear"
              className="form-control"
              value={filters.academicYear}
              onChange={handleFilterChange}
            >
              <option value="">All Years</option>
              <option value="2024-2025">2024-2025</option>
              <option value="2023-2024">2023-2024</option>
              <option value="2022-2023">2022-2023</option>
            </select>
          </div>

          <div className="filter-group">
            <label>Payment Status</label>
            <select
              name="hasPaid"
              className="form-control"
              value={filters.hasPaid}
              onChange={handleFilterChange}
            >
              <option value="">All</option>
              <option value="true">Paid</option>
              <option value="false">Unpaid</option>
            </select>
          </div>

          <div className="filter-group">
            <label>Member Status</label>
            <select
              name="status"
              className="form-control"
              value={filters.status}
              onChange={handleFilterChange}
            >
              <option value="">All</option>
              <option value="Official Member">Official Member</option>
              <option value="Pending">Pending</option>
              <option value="Rejected">Rejected</option>
            </select>
          </div>

          <div className="filter-group">
            <label>Search</label>
            <input
              type="text"
              name="search"
              className="form-control"
              placeholder="Search by name, ID, email..."
              value={filters.search}
              onChange={handleFilterChange}
            />
          </div>
        </div>

        <p style={{ marginTop: '20px', color: '#666' }}>
          Showing {filteredMembers.length} of {members.length} members
        </p>
      </div>

      {loading ? (
        <div className="loading">Loading members...</div>
      ) : (
        <div className="card">
          <div style={{ overflowX: 'auto' }}>
            <table className="table">
              <thead>
                <tr>
                  <th>Student ID</th>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Course</th>
                  <th>Year Level</th>
                  <th>A.Y.</th>
                  <th>Status</th>
                  <th>Payment</th>
                  <th>Amount</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredMembers.length === 0 ? (
                  <tr>
                    <td colSpan="10" className="text-center">No members found</td>
                  </tr>
                ) : (
                  filteredMembers.map(member => (
                    <tr key={member._id}>
                      <td>{member.studentId}</td>
                      <td>{member.firstName} {member.lastName}</td>
                      <td>{member.email}</td>
                      <td>{member.course}</td>
                      <td>{member.yearLevel}</td>
                      <td>{member.academicYear}</td>
                      <td>
                        <span className={`badge badge-${
                          member.status === 'Official Member' ? 'success' :
                          member.status === 'Pending' ? 'warning' : 'danger'
                        }`}>
                          {member.status}
                        </span>
                      </td>
                      <td>
                        <span className={`badge badge-${member.hasPaid ? 'success' : 'warning'}`}>
                          {member.hasPaid ? 'Paid' : 'Unpaid'}
                        </span>
                      </td>
                      <td>{formatCurrency(member.amountPaid)}</td>
                      <td>
                        <div className="actions">
                          <button 
                            className="btn btn-sm btn-primary"
                            onClick={() => openEditModal(member)}
                          >
                            Edit
                          </button>
                          <button 
                            className="btn btn-sm btn-success"
                            onClick={() => openPaymentModal(member)}
                          >
                            Payment
                          </button>
                          <button 
                            className="btn btn-sm btn-danger"
                            onClick={() => handleDeleteMember(member._id)}
                          >
                            Delete
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      )}

      <MemberModal
        isOpen={showMemberModal}
        onClose={() => {
          setShowMemberModal(false);
          setSelectedMember(null);
        }}
        onSubmit={selectedMember ? handleEditMember : handleAddMember}
        member={selectedMember}
      />

      <PaymentModal
        isOpen={showPaymentModal}
        onClose={() => {
          setShowPaymentModal(false);
          setSelectedMember(null);
        }}
        onSubmit={handleUpdatePayment}
        member={selectedMember}
      />
    </div>
  );
};

export default Members;
